import { createSlice } from "@reduxjs/toolkit";

const uiSlice = createSlice({
    name: 'cart',
    initialState: { showCart: false },
    reducers: {
      toggleCart(state) {
        state.showCart = !state.showCart;
      }
    }
  });

  export const cartActions = uiSlice.actions;

  export default uiSlice.reducer;
